ять','шесть','семь', 'восемь','девять'),        array('','одна','две','три','четыре','пять','шесть','семь', 'восемь','девять'),    );    $a20=array('десять','одиннадцать','двенадцать','тринадцать','четырнадцать' ,'пятнадцать','шестнадцать','семнадцать','восемнадцать','девятнадцать');    $tens=array(2=>'двадцать','тридцать','сорок','пятьдесят','шестьдесят','семьдесят' ,'восемьдесят','девяносто');    $hundred=array('','сто','двести','триста','четыреста','пятьсот','шестьсот', 'семьсот','восемьсот','девятьсот');    $unit=array( // Units        array('копейка' ,'копейки' ,'копеек',	 1),        array('рубль'   ,'рубля'   ,'рублей'    ,0),        array('тысяча'  ,'тысячи'  ,'тысяч'     ,1),        array('миллион' ,'миллиона','миллионов' ,0),        array('миллиард','милиарда','миллиардов',0),    );    //    list($rub,$kop) = explode('.',sprintf("%015.2f", floatval($num)));    $out = array();    if (intval($rub)>0) {        foreach(str_split($rub,3) as $uk=>$v) { // by 3 symbols            if (!intval($v)) continue;            $uk = sizeof($unit)-$uk-1; // unit key            $gender = $unit[$uk][3];            list($i1,$i2,$i3) = array_map('intval',str_split($v,1));            // mega-logic            $out[] = $hundred[$i1]; # 1xx-9xx            if ($i2>1) $out[]= $tens[$i2].' '.$ten[$gender][$i3]; # 20-99            else $out[]= $i2>0 ? $a20[$i3] : $ten[$gender][$i3]; # 10-19 | 1-9            // units without rub & kop            if ($uk>1) $out[]= morph($v,$unit[$uk][0],$unit[$uk][1],$unit[$uk][2]);        } //foreach    }    else $out[] = $nul;    $out[] = morph(intval($rub), $unit[1][0],$unit[1][1],$unit[1][2]); // rub    $out[] = $kop.' '.morph($kop,$unit[0][0],$unit[0][1],$unit[0][2]); // kop    return trim(preg_replace('/ {2,}/', ' ', join(' ',$out)));}//Склоняем словоформуfunction morph($n, $f1, $f2, $f5) {    $n = abs(intval($n)) % 100;    if ($n>10 && $n<20) return $f5;    $n = $n % 10;    if ($n>1 && $n<5) return $f2;    if ($n==1) return $f1;    return $f5;}$asd='-';$dsa='.';$per=str_replace('-','.',$per);$per=str_replace(',','.',$per);$per=str_replace(' ','.',$per);$per=num2str($per);$per2=str_replace('-','.',$per2);$per2=str_replace(',','.',$per2);$per2=str_replace(' ','.',$per2);$per2=num2str($per2); //Пропись на вывод$per_1=strtoupper(substr($per,0,1));$per_2=substr($per,1);$per= $per_1.$per_2;$per2_1=strtoupper(substr($per2,0,1));$per2_2=substr($per2,1);$per2= $per2_1.$per2_2;//Конец перевода денежных единиц*/# Число прописьюfunction num_propis($num){ // $num - цело число# Все варианты написания чисел прописью от 0 до 999 скомпануем в один небольшой массив    $m=array(        array('ноль'),        array('-','один','два','три','четыре','пять','шесть','семь','восемь','девять'),        array('десять','одиннадцать','двенадцать','тринадцать','четырнадцать','пятнадцать','шестнадцать','семнадцать','восемнадцать','девятнадцать'),        array('-','-','двадцать','тридцать','сорок','пятьдесят','шестьдесят','семьдесят','восемьдесят','девяносто'),        array('-','сто','двести','триста','четыреста','пятьсот','шестьсот','семьсот','восемьсот','девятьсот'),        array('-','одна','две')    );# Все варианты написания разрядов прописью скомпануем в один небольшой массив    $r=array(        array('...ллион','','а','ов'), // используется для всех неизвестно больших разрядов        array('тысяч','а','и',''),        array('миллион','','а','ов'),        array('миллиард','','а','ов'),        array('триллион','','а','ов'),        array('квадриллион','','а','ов'),        array('квинтиллион','','а','ов')        // ,array(... список можно продолжить    );    if($num==0)return$m[0][0]; # Если число ноль, сразу сообщить об этом и выйти    $o=array(); # Сюда записываем все получаемые результаты преобразования# Разложим исходное число на несколько трехзначных чисел и каждое полученное такое число обработаем отдельно    foreach(array_reverse(str_split(str_pad($num,ceil(strlen($num)/3)*3,'0',STR_PAD_LEFT),3))as$k=>$p){        $o[$k]=array();# Алгоритм, преобразующий трехзначное число в строку прописью        foreach($n=str_split($p)as$kk=>$pp)            if(!$pp)continue;else                switch($kk){                    case 0:$o[$k][]=$m[4][$pp];break;                    case 1:if($pp==1){$o[$k][]=$m[2][$n[2]];break 2;}else$o[$k][]=$m[3][$pp];break;                    case 2:if(($k==1)&&($pp<=2))$o[$k][]=$m[5][$pp];else$o[$k][]=$m[1][$pp];break;                }$p*=1;if(!$r[$k])$r[$k]=reset($r);# Алгоритм, добавляющий разряд, учитывающий окончание руского языка        if($p&&$k)switch(true){            case preg_match("/^[1]$|^\d*[0,2-9][1]$/",$p):$o[$k][]=$r[$k][0].$r[$k][1];break;            case preg_match("/^[2-4]$|\d*[0,2-9][2-4]$/",$p):$o[$k][]=$r[$k][0].$r[$k][2];break;            default:$o[$k][]=$r[$k][0].$r[$k][3];break;        }$o[$k]=implode(' ',$o[$k]);    }    return implode(' ',array_reverse($o));}$test_level_type_name='';if ($act->test_level_type_id==1) $test_level_type_name='о владении русским языком как иностранным';if ($act->test_level_type_id==2) $test_level_type_name='о владении русским языком как иностранным,знании истории России и основ законодательстваРоссийской Федерации';?><div>&nbsp;</div><h3 style="text-align: center">АКТ <!--№___________-->    <br>ОБ ОФОРМЛЕНИИ ДУБЛИКАТА СЕРТИФИКАТА<br><?=$test_level_type_name?></h3><div align=center style="text-align: justify;"><span style="font-size:12.0pt;color:#333333;text-align:justify">        Настоящий акт составлен между        <?=TEXT_HEADCENTER_LONG_TP_PRINT_ACT_NEW?>        (далее по тексту «<?=TEXT_HEADCENTER_SHORT_IP?>»)        и<!--        <br>--><!--        <u>-->        <?=$vuz_name; ?>        <!--        </u>--><!--        <br>-->        (далее по тексту «Организация») к Договору № <!--<u>--><?=$dogovor_num; ?><!--</u>--> от    <!--<u>--><?=strtolower($d